SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_cronograma_npi (nr_seq_proj_cron_etapa_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		integer;
nr_seq_projeto_w		bigint;
nr_seq_fase_processo_prox_w	bigint;
nr_Seq_cronograma_w		bigint;
nm_fase_w			varchar(255);
nm_usuario_recep_w		varchar(255);
ds_dano_w			varchar(4000);


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	proj_cron_etapa a,
	proj_cronograma b,
	prp_processo x
where	a.nr_sequencia = nr_seq_proj_cron_etapa_p
and	a.nr_Seq_cronograma = b.nr_sequencia
and	x.nr_seq_projeto = b.nr_Seq_proj;

if (qt_registros_w > 0) then

	select	y.nr_seq_proj,
		y.nr_sequencia
	into STRICT	nr_seq_projeto_w,
		nr_Seq_cronograma_w
	from	proj_cron_etapa x,
		proj_cronograma y
	where	x.nr_seq_cronograma = y.nr_sequencia
	and	x.nr_sequencia = nr_seq_proj_cron_etapa_p;

	update	proj_cronograma
	set dt_fim_real = clock_timestamp()
	where nr_sequencia = nr_Seq_cronograma_w;

	select	coalesce(min(a.nr_sequencia),6)
	into STRICT	nr_seq_fase_processo_prox_w
	from	proj_cronograma d,
		prp_processo_fase b,
		prp_fase_processo a,
		prp_processo e
	where	d.nr_seq_proj = nr_seq_projeto_w
	and	d.nr_seq_processo_fase = b.nr_sequencia
	and	a.nr_sequencia = b.nr_seq_fase_processo
	and	e.nr_sequencia = b.nr_seq_processo
	and	a.nr_sequencia > e.nr_seq_fase_processo;

	select	max(nm_fase)
	into STRICT	nm_fase_w
	from	prp_fase_processo
	where	nr_sequencia = nr_seq_fase_processo_prox_w;

	/*update	prp_log_processo_fase
	set	nr_seq_fase_processo = nvl(nr_seq_fase_processo_prox_w,nr_seq_fase_processo),
		nr_seq_fase_processo_ant = nr_seq_fase_processo
	where	nr_seq_projeto = nr_seq_projeto_w;	*/
	update	prp_processo
	set	nr_seq_fase_processo = coalesce(nr_seq_fase_processo_prox_w,nr_seq_fase_processo)
	where	nr_seq_projeto = nr_seq_projeto_w;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
