CREATE OR REPLACE PROCEDURE atualizar_custo_proc_mat ( dt_parametro_P timestamp, NM_USUARIO_P text) AS $body$
DECLARE

 
dt_parametro_inicio_w       	timestamp;
dt_parametro_fim_w        	timestamp;
NR_INTERNO_CONTA_W         bigint;

C01 CURSOR FOR 
	SELECT a.nr_interno_conta 
from 	conta_paciente a 
where a.dt_mesano_referencia between dt_parametro_inicio_w and dt_parametro_fim_w 
 and a.ie_status_acerto 	= 2;


BEGIN 
 
dt_parametro_fim_w         := last_day(dt_parametro_p);
dt_parametro_fim_w         := 
    to_date(to_char(dt_parametro_fim_w,'dd/mm/yyyy') || ' 23:59:59', 
    'dd/mm/yyyy hh24:mi:ss');
dt_parametro_Inicio_w		 	:= trunc(dt_parametro_p,'month');
dt_parametro_Inicio_w        := 
    to_date(to_char(dt_parametro_Inicio_w,'dd/mm/yyyy') || ' 00:00:00', 
    'dd/mm/yyyy hh24:mi:ss');
 
OPEN C01;
LOOP 
FETCH C01 into 	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL Atualizar_Tabela_Custo_Conta(nr_interno_conta_w);
	end;
END LOOP;
CLOSE C01;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
