SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dados_checkup ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_seq_exame_w		bigint;
nr_seq_exame_item_w	bigint;
nr_prescricao_w		bigint;
nr_seq_prescricao_w	integer;

qt_resultado_w		double precision;
ds_resultado_w		varchar(255);
pr_resultado_w		double precision;
ie_status_atend_w	bigint := 35;
ie_sem_aprovacao_w	varchar(1);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_seq_exame
	from	checkup_tipo_dado_estat b,
		checkup_dado_estat a
	where	a.nr_seq_tipo_dado	= b.nr_sequencia
	and	a.nr_atendimento	= nr_atendimento_p
	and	coalesce(a.qt_dado::text, '') = ''
	and	coalesce(a.ds_dado::text, '') = ''
	and	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '');


C03 CURSOR FOR
	SELECT 	b.qt_resultado,
		substr(b.ds_resultado,1,255),
		b.pr_resultado,
		b.nr_seq_exame,
		c.nr_prescricao,
		b.nr_seq_prescr
	from 	prescr_medica c,
		exame_lab_result_item b,
		exame_lab_resultado a,
		prescr_procedimento x
	where a.nr_seq_resultado = b.nr_seq_resultado
	and a.nr_prescricao  = c.nr_prescricao
	and x.nr_sequencia  = b.nr_seq_prescr
	and x.nr_prescricao  = c.nr_prescricao
	and b.nr_seq_exame  = nr_seq_exame_w
	and c.nr_atendimento = nr_atendimento_p
	and x.ie_status_atend >= ie_status_atend_w
	and ((b.qt_resultado IS NOT NULL AND b.qt_resultado::text <> '') or (b.ds_resultado IS NOT NULL AND b.ds_resultado::text <> '') or (b.pr_resultado IS NOT NULL AND b.pr_resultado::text <> ''))
	order by b.dt_aprovacao;



BEGIN

ie_sem_aprovacao_w	:= Obter_Valor_Param_Usuario(281,176, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0);
if (ie_sem_aprovacao_w = 'S') then
	ie_status_atend_w	:= 30;
end if;

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w,
	nr_seq_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_prescricao_w		:= 0;
	nr_seq_prescricao_w	:= 0;
	qt_resultado_w		:= 0;
	ds_resultado_w		:= '';
	pr_resultado_w		:= 0;

	OPEN C03;
	LOOP
	FETCH C03 into
		qt_resultado_w,
		ds_resultado_w,
		pr_resultado_w,
		nr_seq_exame_item_w,
		nr_prescricao_w,
		nr_seq_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */

		if (coalesce(qt_resultado_w,0) <> 0) then
			update	checkup_dado_estat
			set	qt_dado		= qt_resultado_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_sequencia_w
			and	nr_seq_tipo_dado = (	SELECT	max(b.nr_sequencia)
							from	checkup_tipo_dado_estat b
							where	nr_seq_exame	= nr_seq_exame_item_w);

		elsif (coalesce(pr_resultado_w,0) <> 0) then
			update	checkup_dado_estat
			set	qt_dado		= pr_resultado_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_sequencia_w
			and	nr_seq_tipo_dado = (	SELECT	max(b.nr_sequencia)
							from	checkup_tipo_dado_estat b
							where	nr_seq_exame	= nr_seq_exame_item_w);

		elsif (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
			update	checkup_dado_estat
			set	ds_dado		= ds_resultado_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_sequencia_w
			and	nr_seq_tipo_dado = (	SELECT	max(b.nr_sequencia)
							from	checkup_tipo_dado_estat b
							where	nr_seq_exame	= nr_seq_exame_item_w);
		end if;

	END LOOP;
	Close C03;

END LOOP;
Close C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_dados_checkup ( nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

