CREATE OR REPLACE PROCEDURE atualizar_dados_envio_prot ( nr_seq_protocolo_p bigint, ds_arquivo_p text, ie_atualiza_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	if (ie_atualiza_p <> 'S') then 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
			nm_usuario_envio = nm_usuario_p, 
			ds_arquivo_envio = ds_arquivo_p 
		where nr_seq_protocolo = nr_seq_protocolo_p;
	else 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
			dt_entrega_convenio = clock_timestamp(), 
			nm_usuario_envio = nm_usuario_p, 
			ds_arquivo_envio = ds_arquivo_p 
		where nr_seq_protocolo = nr_seq_protocolo_p;
	end if;
end if;
 
commit;
 
end;
$body$
