CREATE OR REPLACE PROCEDURE atualizar_dados_escala_dialise (cd_pessoa_fisica_p text, ie_excluir_old_p text DEFAULT 'S') AS $body$
BEGIN
    IF coalesce(ie_excluir_old_p, 'S') = 'S' THEN
        DELETE FROM hd_escala_dialise_temp WHERE cd_pessoa_fisica = cd_pessoa_fisica_p;
    ELSE
        DELETE FROM hd_escala_dialise_temp WHERE trunc(dt_atualizacao) < trunc(clock_timestamp());
    END IF;
    COMMIT;

END;
