CREATE OR REPLACE PROCEDURE atualizar_dados_funcionario ( cd_pessoa_fisica_p text, ie_vinculo_profissional_p text) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin

	update	pessoa_fisica
	set	ie_funcionario		= 'S'
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	if (ie_vinculo_profissional_p IS NOT NULL AND ie_vinculo_profissional_p::text <> '') then
		update	medico
		set	ie_vinculo_medico	= ie_vinculo_profissional_p
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	end if;

	commit;

	end;
end if;

end;
$body$
