		where	nr_sequencia		=	(SELECT		a.nr_seq_dem_ret_prot
							from		imp_dem_retorno_guia a
							where		a.nr_sequencia	= 	(select	b.nr_seq_dem_ret_guia
												from	imp_dem_ret_item b
												where 	b.nr_sequencia = nr_seq_item_p));
		commit;
	end if;

end;
