CREATE OR REPLACE PROCEDURE atualizar_dados_paciente_atend (nr_seq_atendimento_p bigint, nr_seq_sinal_vital_p bigint) AS $body$
DECLARE


qt_peso_w		real;
qt_altura_cm_w		real;



BEGIN

if (nr_seq_sinal_vital_p > 0) and (nr_seq_atendimento_p > 0)	then

	select	qt_peso,
		qt_altura_cm
	into STRICT	qt_peso_w,
		qt_altura_cm_w
	from     atendimento_sinal_vital
	where    nr_sequencia = nr_seq_sinal_vital_p;

	update paciente_atendimento
	set	qt_peso = qt_peso_w,
		qt_altura = qt_altura_cm_w
	where nr_seq_atendimento = nr_seq_atendimento_p;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
