CREATE OR REPLACE PROCEDURE atualizar_dados_prescr_mat_js ( cd_motivo_baixa_p bigint, nr_seq_prescr_mat_p bigint, nr_seq_item_p bigint, cd_material_baixa_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ####   IE_OPCAO_P   #### 			*/

/*ADCD - Atualizar data de baixa e motivo da baixa	*/

/*ACB - Atualizar o c√≥digo de baixa do material 		*/

/*					*/

BEGIN

if (nr_seq_prescr_mat_p IS NOT NULL AND nr_seq_prescr_mat_p::text <> '') and (cd_motivo_baixa_p IS NOT NULL AND cd_motivo_baixa_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '')	 then

	update	prescr_material
	set		dt_baixa		= CASE WHEN ie_opcao_p='ADCD' THEN clock_timestamp() WHEN ie_opcao_p='ACB' THEN null END ,
			cd_motivo_baixa = cd_motivo_baixa_p
	where	nr_sequencia 	= nr_seq_item_p
	and		nr_prescricao 	= nr_seq_prescr_mat_p;

end if;

commit;

end;
$body$
