SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_datas_dev_mat_pac ( nr_devolucao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
==== Opções ====

--Atualizar:
'ALB' - Atualizar  DATA LIBERAÇÃO BAIXA
'ABT' - Atualizar  DATA BAIXA TOTAL
'ALMB' - Atualizar liberação movimento baixa
'ABM' - Atualizar baixa movimento

--Limpar:
'LLB' - Limpar DATA LIBERAÇÃO BAIXA
'LBT' - Limpar DATA BAIXA TOTAL
'LLMB' - Limpar liberação movimento baixa
*/
BEGIN

if (nr_devolucao_p IS NOT NULL AND nr_devolucao_p::text <> '') then

	if (ie_opcao_p = 'ALB') then

		update	devolucao_material_pac
		set	dt_liberacao_baixa	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_devolucao		= nr_devolucao_p;

	elsif (ie_opcao_p = 'LLB') then

		update	devolucao_material_pac
		set	dt_liberacao_baixa	 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_devolucao		= nr_devolucao_p;

	elsif (ie_opcao_p = 'ABT') then

		update	devolucao_material_pac
		set	dt_baixa_total		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_devolucao		= nr_devolucao_p;

	elsif (ie_opcao_p = 'LBT') then

		update	devolucao_material_pac
		set	dt_baixa_total		 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_devolucao		= nr_devolucao_p;

	elsif (ie_opcao_p = 'ALMB') then

		update	farm_devolucao_movimento
		set		dt_liberacao	= clock_timestamp(),
				nm_usuario		= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_devolucao_p;

	elsif (ie_opcao_p = 'LLMB') then

		update	farm_devolucao_movimento
		set		dt_liberacao	 = NULL,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_devolucao_p;

	elsif (ie_opcao_p = 'ABM') then

		update	farm_devolucao_movimento
		set		dt_baixa			= clock_timestamp(),
				nm_usuario_baixa	= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_devolucao_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
