CREATE OR REPLACE PROCEDURE atualizar_datas_etapa_npi ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w	proj_projeto.nr_sequencia%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(b.nr_seq_proj)
	into STRICT	nr_seq_projeto_w
	from	proj_cron_etapa a,
		proj_cronograma b
	where	a.nr_seq_cronograma = b.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p;

	CALL cronograma_padrao_npi.seta_dt_inicio_fim(nr_seq_projeto_w, nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
