SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_adiantamento ( nr_sequencia_dev_p bigint, nr_adiantamento_p bigint, nm_usuario_p text, ie_commit_p text, ie_utiliza_data_dev_p text DEFAULT 'N') AS $body$
DECLARE


dt_atualizacao_w		timestamp;
vl_saldo_w		double precision;
dt_devolucao_w		timestamp;
dt_baixa_w     timestamp;


BEGIN

if (nr_sequencia_dev_p IS NOT NULL AND nr_sequencia_dev_p::text <> '') and (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') then

	select	max(b.dt_atualizacao),
		max(a.vl_saldo),
		max(b.dt_devolucao)
	into STRICT	dt_atualizacao_w,
		vl_saldo_w,
		dt_devolucao_w
	from	adiantamento a,
		adiantamento_dev b
	where	a.nr_adiantamento	= b.nr_adiantamento
	and 	a.nr_adiantamento	= nr_adiantamento_p
	and	b.nr_sequencia		= nr_sequencia_dev_p;

	if (vl_saldo_w = 0)  then --somente atualizar se o adiantamento nao possuir saldo.
        if (ie_utiliza_data_dev_p = 'S') then
            dt_baixa_w := dt_atualizacao_w;
        elsif (ie_utiliza_data_dev_p = 'D') then
            dt_baixa_w := dt_devolucao_w;
        else
            dt_baixa_w := coalesce(dt_atualizacao_w, dt_devolucao_w);
        end if;

		update	adiantamento a
		set	a.dt_baixa	= dt_baixa_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao  = clock_timestamp()
		where	nr_adiantamento	= nr_adiantamento_p;

		if (coalesce(ie_commit_p,'N') = 'S') then -- Commita  somente quando executada direto  no delphi no afterpost no adiantamento_dev_dbp. Essa procedure tb e chamada no Atualizar_Saldo_Adiantamento, e nessa NAO pode commitar.
			commit;
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_data_adiantamento ( nr_sequencia_dev_p bigint, nr_adiantamento_p bigint, nm_usuario_p text, ie_commit_p text, ie_utiliza_data_dev_p text DEFAULT 'N') FROM PUBLIC;

