SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_atend_medico ( nr_atendimento_p bigint) AS $body$
DECLARE

 
qt_existe_w	bigint;


BEGIN 
 
Select	count(*) 
into STRICT	qt_existe_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p 
and	(dt_inicio_atendimento IS NOT NULL AND dt_inicio_atendimento::text <> '') 
and	coalesce(dt_atend_medico::text, '') = '';
 
if (qt_existe_w > 0) then 
	update	atendimento_paciente 
	set	dt_atend_medico	= clock_timestamp() 
	where	nr_atendimento	= nr_atendimento_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
