SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_autenticacao ( nr_titulo_p text, nr_adiantamento_p text, nr_seq_trans_financ_p text ) AS $body$
BEGIN
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	 
	update	titulo_receber_liq 
	set	dt_autenticacao	= clock_timestamp() 
	where	nr_titulo	= nr_titulo_p;
	 
elsif (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') then 
 
	update	adiantamento 
	set	dt_autenticacao	= clock_timestamp() 
	where	nr_adiantamento	= nr_adiantamento_p;
	 
elsif (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') then 
 
	update	movto_trans_financ 
	set	dt_autenticacao	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_trans_financ_p;
	 
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
