CREATE OR REPLACE PROCEDURE atualizar_data_bo (nm_usuario_envio_p text, nr_sequencia_p text, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'A')then
begin
	update 	sac_boletim_ocorrencia
	set 	dt_envio = clock_timestamp(),
		nm_usuario_envio = nm_usuario_envio_p
	where 	nr_sequencia = nr_sequencia_p;

end;
else
begin
	update 	sac_boletim_ocorrencia
	set 	dt_reenvio = clock_timestamp(),
		nm_usuario_reenvio = nm_usuario_envio_p
	where 	nr_sequencia = nr_sequencia_p;
end;
end if;

commit;

end;
$body$
