CREATE OR REPLACE PROCEDURE atualizar_data_digitacao_atend ( nr_atendimento_p bigint, dt_digitacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_resultado_w	bigint;

C01 CURSOR FOR
	SELECT	distinct e.nr_seq_resultado
	from 	exame_lab_resultado e,
			prescr_procedimento b,
			prescr_medica a
	where 	a.nr_prescricao = b.nr_prescricao
	and 	b.nr_prescricao = e.nr_prescricao
	and 	b.ie_suspenso = 'N'
	and 	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '')
	and 	b.ie_status_atend >= 29
	and 	a.nr_atendimento = nr_atendimento_p
	and 	not exists (SELECT 1 from lab_restringe_result_etapa where ie_etapa = b.ie_status_atend);


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_resultado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update 	exame_lab_result_item
		set 	dt_digitacao = dt_digitacao_p
		where	nr_seq_resultado = nr_seq_resultado_w;
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
