SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_geracao_lab (nr_prescricao_p text, nr_seq_prescr_p bigint, ie_opcao_p text) AS $body$
BEGIN

/*	Opção

	S - Preencher data de geração do arquivo
	N - Retirar data de geração do arquivo

	*/
if (ie_opcao_p = 'S') then

	update	prescr_procedimento
	set	dt_geracao_arquivo	= clock_timestamp()
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_seq_prescr_p;

elsif (ie_opcao_p = 'N') then

	update	prescr_procedimento
	set	dt_geracao_arquivo	 = NULL
	where	nr_prescricao		= nr_prescricao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
