CREATE OR REPLACE PROCEDURE atualizar_data_interrupcao ( nr_cirurgia_p bigint, dt_interrupcao_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then
	update	cirurgia
	set	dt_interrupcao 		= dt_interrupcao_p,
		ie_status_cirurgia	= 4,
		nm_usuario		= nm_usuario_p
	where	nr_cirurgia	= nr_cirurgia_p;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
