CREATE OR REPLACE PROCEDURE atualizar_data_prevista_os ( nm_usuario_prev_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
		 
nr_sequencia_w				bigint;
dt_prevista_w				timestamp;
dt_prevista_ww				timestamp;
		
c01 CURSOR FOR 
SELECT	a.nr_sequencia, 
	a.dt_prevista +1 
from	man_ordem_ativ_prev a, 
	man_ordem_servico_v c 
where	upper(a.nm_usuario_prev)	= upper(nm_usuario_prev_p) 
and	c.nr_sequencia 			= a.nr_seq_ordem_serv 
and	trunc(a.dt_prevista) 		> fim_dia(clock_timestamp());
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	dt_prevista_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	 
	dt_prevista_ww	:=	OBTER_PROXIMO_DIA_UTIL(cd_estabelecimento_p,dt_prevista_w);
		 
		 
	update	man_ordem_ativ_prev 
	set	dt_prevista = dt_prevista_ww 
	where	nr_sequencia = nr_sequencia_w;	
	 
	commit;
	 
	end;
end loop;
close C01;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
