SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_prev_os_html5 ( nm_usuario_p text, cd_projeto_p bigint) AS $body$
DECLARE






C01 CURSOR FOR
SELECT  	c.nr_sequencia nr_seq_os,
       	 d.nr_sequencia nr_seq_atividade,
       	 d.dt_prevista,
	b.nr_sequencia nr_seq_etapa,
        	b.dt_inicio_prev
from    	man_ordem_ativ_prev	d,
	man_ordem_servico	c,
	proj_cron_etapa		b,
	proj_cronograma		a
where	a.nr_sequencia	= b.nr_seq_cronograma
and	c.nr_sequencia		= d.nr_seq_ordem_serv
and	c.nr_seq_proj_cron_etapa = b.nr_sequencia
and	a.nr_seq_proj		=  cd_projeto_p;

c01_w c01%rowtype;
cd_programador_w	varchar(10);
nm_usuario_w		varchar(15)	:= 'X';



BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	select	coalesce(max(cd_programador),'X')
	into STRICT	cd_programador_w
	from	proj_cron_etapa_equipe
	where	nr_seq_etapa_cron	= c01_w.nr_seq_etapa;
	exception
	when others then
		cd_programador_w	:= 'X';
	end;

	if (cd_programador_w <> 'X') then
		nm_usuario_w	:= obter_usuario_pessoa(cd_programador_w);

		update	man_ordem_servico
		set	cd_pessoa_solicitante	= cd_programador_w
		where	nr_sequencia		= c01_w.nr_seq_os;
	else
		nm_usuario_w	:= 'X';
	end if;

	update	man_ordem_ativ_prev
	set	dt_prevista	= c01_w.dt_inicio_prev,
		nm_usuario_prev	= CASE WHEN nm_usuario_w='X' THEN nm_usuario_prev  ELSE nm_usuario_w END ,
		nm_usuario 	= nm_usuario_p,
		dt_atualizacao 	= clock_timestamp()
	where	nr_sequencia	= c01_w.nr_seq_atividade;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_data_prev_os_html5 ( nm_usuario_p text, cd_projeto_p bigint) FROM PUBLIC;

