CREATE OR REPLACE PROCEDURE atualizar_data_quimio_liberada ( nr_seq_atendimento_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


/*
Desfazer		D
*/
BEGIN

IF (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	begin
	if (ie_opcao_p = 'D') then
		begin
		update	paciente_atendimento
		set	dt_quimio_liberada  = NULL,
			nm_usuario = nm_usuario_p
		where	nr_seq_atendimento = nr_seq_atendimento_p;

		commit;
		end;
	else
		begin
		update	paciente_atendimento
		set	dt_quimio_liberada = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_seq_atendimento = nr_seq_atendimento_p;

		commit;

		end;
	end if;
	end;
end if;

commit;

end;
$body$
