CREATE OR REPLACE PROCEDURE atualizar_data_resultado_ficha ( nr_prescricao_p bigint, nr_sequencia_p bigint, dt_resultado_p timestamp, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_resultado_w timestamp;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then
	begin

	update 	prescr_procedimento
	set 	dt_resultado 	= dt_resultado_p,
	        nm_usuario = nm_usuario_p,
        	dt_atualizacao = clock_timestamp()
	where  	nr_sequencia 	= nr_sequencia_p
	and    	nr_prescricao 	= nr_prescricao_p;


	commit;

        select max(dt_resultado)
	into STRICT dt_resultado_w
	from prescr_procedimento
        where nr_prescricao = nr_prescricao_p;

        update prescr_medica
        set    dt_entrega = dt_resultado_w,
               nm_usuario = nm_usuario_p,
               dt_atualizacao = clock_timestamp()
        where  nr_prescricao = nr_prescricao_p;



	end;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
