SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_data_retorno_doador ( nr_seq_doador_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
dt_retorno_w		timestamp;


BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from 	san_doacao
where	nr_sequencia = nr_seq_doador_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	dt_retorno_w	:= obter_data_retorno_doador(cd_pessoa_fisica_w);
	update	san_doacao
	set	dt_retorno 	= dt_retorno_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia = nr_seq_doador_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
