CREATE OR REPLACE PROCEDURE atualizar_deducao_conv_conta ( nr_seq_deducao_p bigint, vl_deducao_p bigint, ie_base_imposto_p text, nr_seq_conta_des_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_base_imposto_w	conv_regra_calculo_conta.ie_base_imposto%type;


BEGIN

if (coalesce(nr_seq_deducao_p,0) > 0) then
	ie_base_imposto_w	:= coalesce(ie_base_imposto_p,'A');

	update	conta_pac_deducao_conv
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		vl_calculado = CASE WHEN ie_base_imposto_w='A' THEN vl_deducao_p  ELSE vl_calculado END ,
		vl_calculado_com_imp	= CASE WHEN ie_base_imposto_w='BCI' THEN vl_deducao_p  ELSE vl_calculado_com_imp END ,
		vl_calculado_sem_imp	= CASE WHEN ie_base_imposto_w='BSI' THEN vl_deducao_p  ELSE vl_calculado_sem_imp END ,
		dt_processamento = clock_timestamp(),
		nr_seq_conta_des = coalesce(nr_seq_conta_des_p, nr_seq_conta_des)
	where	nr_sequencia = nr_seq_deducao_p;

	update	conta_pac_deducao_conv
	set		vl_calculado = coalesce(vl_calculado_com_imp,0) + coalesce(vl_calculado_sem_imp,0)
	where	nr_sequencia = nr_seq_deducao_p
	and ((vl_calculado_com_imp IS NOT NULL AND vl_calculado_com_imp::text <> '') or (vl_calculado_sem_imp IS NOT NULL AND vl_calculado_sem_imp::text <> ''));

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
