SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_desconto_bordero ( nr_titulo_p bigint, nr_bordero_p bigint, vl_desconto_w bigint, nm_usuario_p text) AS $body$
DECLARE


cd_centro_custo_w	titulo_receber_liq_desc.cd_centro_custo%type;
nr_seq_motivo_desc_w	titulo_receber_liq_desc.nr_seq_motivo_desc%type;
vl_abaixar_w		bordero_tit_rec.vl_abaixar%type;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then

	select	max(a.cd_centro_custo) cd_centro_custo_desc,
		max(a.nr_seq_motivo_desc) nr_seq_motivo_desc
	into STRICT	cd_centro_custo_w,
		nr_seq_motivo_desc_w
	from    titulo_receber_liq_desc a
	where   a.nr_titulo    = nr_titulo_p
	and     coalesce(a.nr_seq_liq::text, '') = ''
	and     coalesce(a.nr_bordero::text, '') = '';

	if (cd_centro_custo_w IS NOT NULL AND cd_centro_custo_w::text <> '') and (nr_seq_motivo_desc_w IS NOT NULL AND nr_seq_motivo_desc_w::text <> '') then
	/*Somente fazer update se o centro de custo e o motivo nao forem nulos. Isso para ficar igual ocorre na manutencao de titulo a receber*/

		select	coalesce(max(a.vl_abaixar),0)
		into STRICT	vl_abaixar_w
		from	bordero_tit_rec a
		where	nr_titulo		= nr_titulo_p
		and	nr_bordero		= nr_bordero_p;

		update	bordero_tit_rec a
		set	vl_desconto		= coalesce(vl_desconto_w,0),
			nr_seq_motivo_desc	= nr_seq_motivo_desc_w,
			cd_centro_custo_desc	= cd_centro_custo_w,
			vl_abaixar		= coalesce(vl_abaixar_w,0) - coalesce(vl_desconto_w,0)
		where	nr_titulo		= nr_titulo_p
		and	nr_bordero		= nr_bordero_p;

		commit;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_desconto_bordero ( nr_titulo_p bigint, nr_bordero_p bigint, vl_desconto_w bigint, nm_usuario_p text) FROM PUBLIC;

