CREATE OR REPLACE PROCEDURE atualizar_desc_bordero_rec (nr_bordero_p bigint, nr_titulo_p bigint, cd_centro_custo_desc_p bigint, nr_seq_motivo_desc_p bigint, cd_pf_solic_desc_p text, cd_cgc_solic_desc_p text, nm_usuario_p text) AS $body$
DECLARE


/* Nao colocar commit nessa procedure, ela é chamada varias vezes em um laço */

BEGIN

if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	if (cd_pf_solic_desc_p IS NOT NULL AND cd_pf_solic_desc_p::text <> '') and (cd_cgc_solic_desc_p IS NOT NULL AND cd_cgc_solic_desc_p::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265464,'');
		--O solicitante deve ser uma pessoa física ou uma pessoa jurídica!
	end if;

	update	bordero_tit_rec
	set	cd_centro_custo_desc	= cd_centro_custo_desc_p,
		nr_seq_motivo_desc	= nr_seq_motivo_desc_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_bordero		= nr_bordero_p
	and	nr_titulo		= nr_titulo_p;

	if (cd_pf_solic_desc_p IS NOT NULL AND cd_pf_solic_desc_p::text <> '') or (cd_cgc_solic_desc_p IS NOT NULL AND cd_cgc_solic_desc_p::text <> '') then
		delete	from titulo_receber_liq_desc
		where	nr_bordero		= nr_bordero_p
		and	nr_titulo		= nr_titulo_p;

		insert	into	titulo_receber_liq_desc(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_bordero,
			nr_titulo,
			cd_pessoa_fisica,
			cd_cgc,
			nr_seq_motivo_desc,
			cd_centro_custo)
		values (nextval('titulo_receber_liq_desc_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_bordero_p,
			nr_titulo_p,
			cd_pf_solic_desc_p,
			cd_cgc_solic_desc_p,
			nr_seq_motivo_desc_p,
			cd_centro_custo_desc_p);
	end if;
end if;

end;
