SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_devolucao_tit ( nr_seq_devolucao_rec_p bigint, nr_seq_devolucao_pag_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w	bigint;
dt_devolucao_w	timestamp;
dt_retorno_w	timestamp;


BEGIN

if (nr_seq_devolucao_rec_p IS NOT NULL AND nr_seq_devolucao_rec_p::text <> '') then
	select	a.nr_titulo,
		a.dt_devolucao,
		a.dt_reenvio
	into STRICT	nr_titulo_w,
		dt_devolucao_w,
		dt_retorno_w
	from	titulo_receber_devolucao a
	where	a.nr_sequencia	= nr_seq_devolucao_rec_p;

	if (coalesce(dt_retorno_w::text, '') = '') then
		update	titulo_receber
		set	dt_ultima_devolucao	= dt_devolucao_w
		where	nr_titulo		= nr_titulo_w;
	else
		update	titulo_receber
		set	dt_ultima_devolucao	 = NULL
		where	nr_titulo		= nr_titulo_w;
	end if;

elsif (nr_seq_devolucao_pag_p IS NOT NULL AND nr_seq_devolucao_pag_p::text <> '') then
	select	a.nr_titulo,
		a.dt_devolucao,
		a.dt_retorno
	into STRICT	nr_titulo_w,
		dt_devolucao_w,
		dt_retorno_w
	from	titulo_pagar_devolucao a
	where	a.nr_sequencia	= nr_seq_devolucao_pag_p;

	if (coalesce(dt_retorno_w::text, '') = '') then
		update	titulo_pagar
		set	dt_ultima_devolucao	= dt_devolucao_w
		where	nr_titulo		= nr_titulo_w;
	else
		update	titulo_pagar
		set	dt_ultima_devolucao	 = NULL
		where	nr_titulo		= nr_titulo_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
