CREATE OR REPLACE PROCEDURE atualizar_diagnostico_doenca ( nr_atendimento_p bigint, dt_diagnostico_p timestamp, cd_doenca_p text, nm_usuario_p text, ds_justificativa_p text) AS $body$
DECLARE


ie_liberado_w	varchar(1);

BEGIN
select	CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN 'N'  ELSE 'S' END
into STRICT	ie_liberado_w
from	diagnostico_doenca
where	nr_atendimento = nr_atendimento_p
and	dt_diagnostico = dt_diagnostico_p
and	cd_doenca      = cd_doenca_p;

if (ie_liberado_w = 'N') then
	begin
	update	diagnostico_doenca
	set	dt_liberacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_atendimento = nr_atendimento_p
	and	dt_diagnostico = dt_diagnostico_p
	and	cd_doenca      = cd_doenca_p;
	end;
else
	begin
	update	diagnostico_doenca
	set	dt_inativacao = clock_timestamp(),
		nm_usuario_inativacao = nm_usuario_p,
		ds_justificativa = ds_justificativa_p
	where	nr_atendimento = nr_atendimento_p
	and	dt_diagnostico = dt_diagnostico_p
	and	cd_doenca      = cd_doenca_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
