CREATE OR REPLACE PROCEDURE atualizar_dias_ccih_atx ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w			bigint;
nr_seq_w					bigint;
ie_info_rastre_prescr_w		varchar(1);
ds_alteracao_rastre_w		varchar(1800);
BEGIN

	ie_info_rastre_prescr_w := obter_se_info_rastre_prescr('UA', nm_usuario_p, obter_perfil_ativo, obter_estabelecimento_ativo);

	if (ie_info_rastre_prescr_w = 'S') then

		ds_alteracao_rastre_w := substr(obter_desc_expressao(942996) || ' / atualizar_dias_ccih = ' || chr(13)
										|| ' nr_prescricao_p: ' || nr_prescricao_p
										|| ' nr_sequencia_p: ' || nr_sequencia_p
										|| ' cd_material_p: ' || cd_material_p
										|| ' nm_usuario_p: ' || nm_usuario_p, 1, 1800);

		CALL gerar_log_prescr_mat(nr_prescricao_p, nr_sequencia_p, null, null, 'M', ds_alteracao_rastre_w, nm_usuario_p, 'N');

	end if;

	select	nextval('rep_controle_atb_seq')
	into STRICT	nr_seq_w
	;

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;

	insert into rep_controle_atb(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_material,
		nr_dias_lib,
		cd_material,
		nr_atendimento,
		ie_continuou)
	values (nr_seq_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_prescricao_p,
		nr_sequencia_p,
		0,
		cd_material_p,
		nr_atendimento_w,
		'N');

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
