CREATE OR REPLACE PROCEDURE atualizar_doador_liberado (nr_sequencia_p bigint, cd_pessoa_fisica_p text, ie_status_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_status_p = 'EN') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update 	san_doacao
	set 		cd_pessoa_entrega 	= cd_pessoa_fisica_p,
			nm_usuario 	  	= nm_usuario_p,
			dt_entrega_exame  	= clock_timestamp()
	where 	nr_sequencia 	= nr_sequencia_p;

elsif (ie_status_p = 'RE') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update 	san_doacao
	set 		cd_pessoa_recebimento = cd_pessoa_fisica_p,
			nm_usuario 	= nm_usuario_p
	where 	nr_sequencia 	= nr_sequencia_p;
end if;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
