SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_documento_paciente ( nr_seq_document_id_p text, ds_document_p text) AS $body$
DECLARE

													
nr_sequencia_w 	imp_sumario_pac_arquivo.nr_sequencia%type;
	

BEGIN

if (nr_seq_document_id_p IS NOT NULL AND nr_seq_document_id_p::text <> '') then

	select 	max(nr_sequencia)
	into STRICT 	nr_sequencia_w
	from 	imp_sumario_pac_arquivo
	where 	ds_oid_document = nr_seq_document_id_p
	and 	ie_anexado = 'N';
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	
		update 	imp_sumario_pac_arquivo
		set 	ds_conteudo = ds_document_p,
				ie_anexado = 'S'
		where 	nr_sequencia = nr_sequencia_w;
		
		commit;
	
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
