nr_interno_conta_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo	= nr_seq_protocolo_p 
	and	coalesce(nr_conta_convenio::text, '') = '';


