CREATE OR REPLACE PROCEDURE atualizar_dose_insert_mat_cicl (nr_seq_atendimento_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE


  ie_param1556_w               varchar(1);


  c01 CURSOR FOR
    SELECT paciente_atend_medic.nr_seq_material,
           paciente_atend_medic.ie_regra_diluicao_cad_mat
      from paciente_atend_medic
     where paciente_atend_medic.nr_seq_atendimento = nr_seq_atendimento_p
       and paciente_atend_medic.nr_seq_material = nr_seq_material_p
       and paciente_atend_medic.ie_regra_diluicao_cad_mat in ('S','A',CASE WHEN ie_param1556_w='S' THEN 'N'  ELSE null END );
BEGIN
  ie_param1556_w := obter_param_usuario(281, 1556, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param1556_w);

  
  for i in c01 loop
    begin
      delete from paciente_atend_medic
       where nr_seq_diluicao = i.nr_seq_material
         and nr_seq_atendimento = nr_seq_atendimento_p;


      if (coalesce(i.ie_regra_diluicao_cad_mat,'S') = 'S') then
        CALL gerar_reconst_diluicao_ciclo(nr_seq_atendimento_p,i.nr_seq_material,'D',wheb_usuario_pck.get_nm_usuario);
      else
        CALL gerar_reconst_diluicao_ciclo(nr_seq_atendimento_p,i.nr_seq_material,'A',wheb_usuario_pck.get_nm_usuario);
      end if;

    exception
      when others then
        null;
    end;
  end loop;

  commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
