CREATE OR REPLACE PROCEDURE atualizar_dose_soluc_onc_html ( nr_seq_paciente_p bigint, qt_dose_prescricao_p bigint, nr_ciclo_p bigint, cd_material_p bigint, nr_seq_interno_p bigint, nm_usuario_p text, pr_reducao_p text, cd_unid_med_prescr_p text, ie_atualizar_ciclos_p text default 'N') AS $body$
DECLARE


nr_seq_atendimento_w	bigint;
qt_dose_w				double precision;
cd_unid_med_dose_w 		varchar(20);
					
C01 CURSOR FOR
	SELECT 	nr_seq_atendimento
	from   	paciente_atendimento
	where  	nr_seq_paciente = nr_seq_paciente_p
	and    	coalesce(nr_prescricao::text, '') = ''
	and	(((nr_ciclo = nr_ciclo_p) and ie_atualizar_ciclos_p = 'N')
        or 	((nr_ciclo <> nr_ciclo_p) and ie_atualizar_ciclos_p = 'S'));
	

BEGIN
	select	max(qt_dose),
			max(cd_unid_med_dose)
	into STRICT	qt_dose_w,
			cd_unid_med_dose_w
	from	paciente_atend_medic
	where	nr_seq_interno = nr_seq_interno_p;

	open C01;
		loop
		fetch C01 into	
			nr_seq_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			update 	paciente_atend_medic a
			set		a.qt_dose_prescricao 	= qt_dose_prescricao_p,
					a.qt_dose		= coalesce(qt_dose_w,a.qt_dose),
					a.nm_usuario	     	= nm_usuario_p	,
					a.pr_reducao 	= pr_reducao_p,
					a.cd_unid_med_prescr = cd_unid_med_prescr_p,
					a.cd_unid_med_dose = coalesce(cd_unid_med_dose_w, a.cd_unid_med_dose)
			where 		coalesce(nr_seq_diluicao::text, '') = ''
			and		(nr_seq_solucao IS NOT NULL AND nr_seq_solucao::text <> '')
			and		nr_seq_atendimento 	= nr_seq_atendimento_w
			and		cd_material 		= cd_material_p;
			
			CALL atualizar_dose_onc_ciclo(nr_seq_atendimento_w);
			
		end loop;
	close C01;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
