SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_ds_imunohist_laudo (NR_LAUDO_P bigint, DS_IMUNOHIST_P text) AS $body$
DECLARE

IE_EXISTE_REGISTRO smallint;


BEGIN
  SELECT coalesce(MAX(1), 0)
  INTO STRICT IE_EXISTE_REGISTRO
  FROM LAUDO_PACIENTE_COMPL
  WHERE NR_SEQ_LAUDO = NR_LAUDO_P;

  IF (IE_EXISTE_REGISTRO = 1) THEN
    UPDATE LAUDO_PACIENTE_COMPL
    SET DS_IMUNOHISTOQUIMICA = DS_IMUNOHIST_P,
            DT_ATUALIZACAO = clock_timestamp(),
            NM_USUARIO = OBTER_USUARIO_ATIVO
    WHERE NR_SEQ_LAUDO = NR_LAUDO_P;
  ELSE
    INSERT INTO LAUDO_PACIENTE_COMPL(NR_SEQUENCIA,
                                       NR_SEQ_LAUDO,
                                       DT_ATUALIZACAO,
                                       NM_USUARIO,
                                       DT_ATUALIZACAO_NREC,
                                       NM_USUARIO_NREC,
                                       DS_IMUNOHISTOQUIMICA)
    VALUES (nextval('laudo_paciente_compl_seq'),
             NR_LAUDO_P,
             clock_timestamp(),
             OBTER_USUARIO_ATIVO,
             clock_timestamp(),
             OBTER_USUARIO_ATIVO,
             DS_IMUNOHIST_P);
  END IF;

  COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
