CREATE OR REPLACE PROCEDURE atualizar_dt_ant_result_eup ( nr_prescricao_p bigint, nr_sequencia_p bigint, dt_antecipa_result_p timestamp, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
	A - Antecipação
	D - Desfazer antecipação

*/
BEGIN

if (ie_opcao_p = 'A') then
	update	prescr_procedimento
	set	dt_antecipa_result     = dt_antecipa_result_p,
		nm_usuario	       = nm_usuario_p,
		nm_usuario_antecipacao = nm_usuario_p
	where	nr_sequencia	       = nr_sequencia_p
	and	nr_prescricao	       = nr_prescricao_p;
elsif (ie_opcao_p = 'D') then
	update	prescr_procedimento
	set	dt_antecipa_result	 = NULL,
		nm_usuario_antecipacao   = NULL,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p
	and	nr_prescricao	= nr_prescricao_p;
end if;

commit;

end;
