SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dt_autorizacao_cir ( cd_opcao_p bigint, nr_seq_autor_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
opções
1 -  atualiza a data de autorização para a data atual.
2 - limpa a data  de autorização
*/
BEGIN

if (nr_seq_autor_cirurgia_p IS NOT NULL AND nr_seq_autor_cirurgia_p::text <> '') then

	if (cd_opcao_p = 1) then
		update	autorizacao_cirurgia
		set	dt_autorizacao		= clock_timestamp(),
			ie_estagio_autor	= 3,
			nm_usuario_autorizacao	= nm_usuario_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_autor_cirurgia_p;

	elsif (cd_opcao_p = 2) then
		update	autorizacao_cirurgia
		set	dt_autorizacao		 = NULL,
			ie_estagio_autor	= 2,
			nm_usuario_autorizacao	 = NULL,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_autor_cirurgia_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
