SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dt_emissao_relat (nr_prescricao_p bigint, nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prescr_w		bigint;
cd_perfil_ativo_w	bigint;

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from 	prescr_procedimento a
	where 	nr_prescricao = nr_prescricao_p
	and (obter_se_contido(a.nr_sequencia, nr_sequencia_p) = 'S' or (coalesce(nr_sequencia_p::text, '') = ''));


BEGIN

delete 	FROM w_lab_mapa_matricial
where	nr_prescricao	= nr_prescricao_p;

select  OBTER_PERFIL_ATIVO
into STRICT	cd_perfil_ativo_w
;

/*insert into logxxxxx_tasy (dt_atualizacao, nm_usuario, cd_log, ds_log)
values(sysdate,nm_usuario_p,74023,'Prescricao ' || nr_prescricao_p || '-' || nr_sequencia_p || '-Perfil' || cd_perfil_ativo_w);*/
OPEN C01;
LOOP
FETCH C01 into
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	delete 	FROM w_lab_mapa_matricial
	where	nr_seq_prescr	= nr_seq_prescr_w
	and	nr_prescricao	= nr_prescricao_p;

	insert into w_lab_mapa_matricial(nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						nr_seq_prescr,
						nr_prescricao  )
				values (nextval('w_lab_mapa_matricial_seq'),
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_prescr_w,
						nr_prescricao_p);

	update	prescr_procedimento
	set 	dt_emissao_setor_atend = clock_timestamp()
	where	nr_sequencia	= nr_seq_prescr_w
	and	nr_prescricao	= nr_prescricao_p
	and   	coalesce(dt_emissao_setor_atend::text, '') = '';

	end;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
