SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dt_fim_digit_laudo ( nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then
	update	laudo_paciente
	set	dt_fim_digitacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nm_usuario_fim_digit = nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (ie_opcao_p = 'D') then
	update	laudo_paciente
	set	dt_fim_digitacao	 = NULL,
		nm_usuario	= nm_usuario_p,
		nm_usuario_fim_digit  = NULL
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
