SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dt_fim_real_cron ( nm_usuario_p text, nr_seq_cronograma_p bigint) AS $body$
DECLARE


qt_etapa_nao_con_w	bigint;


BEGIN

select	count(nr_sequencia) -- pega a quantidade de etapas com porcentagem de conclusão menor que 100%
into STRICT 	qt_etapa_nao_con_w
from   	proj_cron_etapa
where  	nr_seq_cronograma = nr_seq_cronograma_p
and    	pr_etapa < 100;

	if (qt_etapa_nao_con_w <= 0 )then -- Se estiver 0 é porque todas as etapas do cronograma estão 100% concluídas
		begin
			update proj_cronograma a
			set	dt_fim_real = clock_timestamp(),
				nm_usuario = nm_usuario_p
			where a.nr_sequencia = nr_seq_cronograma_p;
		end;
	else
		begin
			update proj_cronograma a
			set dt_fim_real  = NULL,
				nm_usuario = nm_usuario_p
			where a.nr_sequencia = nr_seq_cronograma_p;
		end;
	end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_dt_fim_real_cron ( nm_usuario_p text, nr_seq_cronograma_p bigint) FROM PUBLIC;

