CREATE OR REPLACE PROCEDURE atualizar_dt_lib_conta_pagar ( dt_liberacao_p timestamp, nr_titulo_p bigint, nr_bordero_p bigint, nr_seq_banco_escrit_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then
	begin
	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
		begin
		update	conta_pagar_lib
		set	dt_liberacao	= dt_liberacao_p,
			nm_usuario	= nm_usuario_p
		where	nr_titulo	= nr_titulo_p
		and	nr_bordero	= nr_bordero_p
		and	nm_usuario_lib	= nm_usuario_p;
		end;
	else
		begin
		update	conta_pagar_lib
		set	dt_liberacao	= dt_liberacao_p,
			nm_usuario	= nm_usuario_p
		where	coalesce(nr_titulo::text, '') = ''
		and	nr_bordero	= nr_bordero_p
		and    nm_usuario_lib	= nm_usuario_p;
		end;
	end if;
	end;
else
	begin
	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
		begin
		update	conta_pagar_lib
		set	dt_liberacao		= dt_liberacao_p,
			nm_usuario		= nm_usuario_p
		where	nr_titulo		= nr_titulo_p
		and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p
		and	nm_usuario_lib		= nm_usuario_p;
		end;
	else
		begin
		update	conta_pagar_lib
		set	dt_liberacao		= dt_liberacao_p,
			nm_usuario		= nm_usuario_p
		where	coalesce(nr_titulo::text, '') = ''
		and	nr_seq_banco_escrit	= nr_seq_banco_escrit_p
		and	nm_usuario_lib		= nm_usuario_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
