CREATE OR REPLACE PROCEDURE atualizar_dt_referencia_tiss (nr_seq_protocolo_p bigint, dt_referencia_tiss_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_justificativa_p text) AS $body$
DECLARE

 
		 
ie_grava_log_w			varchar(01);
vl_protocolo_w			double precision;
dt_referencia_tiss_w		timestamp;
ds_texto_w			protocolo_convenio_log.ds_observacao%type;


BEGIN 
 
ie_grava_log_w := Obter_Param_Usuario(85, 141, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_grava_log_w);
 
select dt_referencia_tiss 
into STRICT	dt_referencia_tiss_w 
from	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
update	protocolo_convenio 
set	dt_referencia_tiss	= dt_referencia_tiss_p, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
if (ie_grava_log_w = 'S') then 
 
		select 	coalesce(obter_total_protocolo(nr_seq_protocolo_p),0)		 
		into STRICT	vl_protocolo_w 
		;
   
		--Data ReferÃªncia TISS(De: #@DT_REFERENCIA_TISS#@ p/ #@DT_REFERENCIA_TISS_P#@ ) 
		ds_texto_w := substr(wheb_mensagem_pck.get_texto(352593,'DT_REFERENCIA_TISS='|| dt_referencia_tiss_w || 
								';DT_REFERENCIA_TISS_P='|| dt_referencia_tiss_p ),1,255);
		 
		insert into protocolo_convenio_log(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_protocolo, 
			ds_arquivo_envio, 
			vl_protocolo, 
			ie_tipo_log, 
			ds_observacao, 
			ds_justificativa) 
		SELECT 	nextval('protocolo_convenio_log_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			a.nr_seq_protocolo, 
			a.ds_arquivo_envio, 
			vl_protocolo_w, 
			'D', 
			ds_texto_w, 
			substr(ds_justificativa_p,1,255) 
		from	protocolo_convenio a 
		where	a.nr_seq_protocolo = nr_seq_protocolo_p;
   
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
