CREATE OR REPLACE PROCEDURE atualizar_dt_repre_cheque ( dt_reapresentacao_p timestamp, nr_seq_cheque_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_devolucao_w	timestamp;


BEGIN

if (dt_reapresentacao_p IS NOT NULL AND dt_reapresentacao_p::text <> '') and (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select max(dt_devolucao_banco)
	into STRICT dt_devolucao_w
	from cheque_cr
	where nr_seq_cheque	= nr_seq_cheque_p;
	-- Verifica se a data de reapresentação é maior ou igual a data de devolução, se reapresentação for menor retorna erro
	if dt_reapresentacao_p >= dt_devolucao_w then
		update 	cheque_cr
		set    	dt_reapresentacao = dt_reapresentacao_p,
			nm_usuario        = nm_usuario_p,
			dt_atualizacao    = clock_timestamp()
		where  	nr_seq_cheque     = nr_seq_cheque_p;

		CALL gerar_cheque_cr_hist(nr_seq_cheque_p,wheb_mensagem_pck.get_texto(304955),'N',nm_usuario_p);
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(339524);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
