SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_dt_revisao ( nm_usuario_p text, nr_seq_contrato_p bigint) AS $body$
DECLARE


dt_revisao_w		timestamp;
dt_rescisao_w		timestamp;
nr_seq_renovacao_w	bigint;


---- Atualiza o campo data de revisão do contrato
---- Dt_revisao = dt_fim_renovacao - b.qt_dias_aviso_venc    mhbarth
-- Pietro OS 526544: Inserido também para atualizar o campo Data fim (Rescisão) do contrato.
BEGIN

select	distinct max(a.nr_sequencia)
into STRICT	nr_seq_renovacao_w
from	contrato_renovacao a,
	contrato b
where	a.nr_seq_contrato = b.nr_sequencia
and	a.nr_seq_contrato = nr_seq_contrato_p;

select	distinct a.dt_fim_renovacao - b.qt_dias_aviso_venc,
	a.dt_fim_renovacao
into STRICT	dt_revisao_w,
	dt_rescisao_w
from	contrato_renovacao a,
	contrato b
where	a.nr_seq_contrato = b.nr_sequencia
and	a.nr_sequencia = nr_seq_renovacao_w
and	b.nr_sequencia = nr_seq_contrato_p;


update	contrato
set	dt_revisao = dt_revisao_w,
	dt_fim = dt_rescisao_w,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_contrato_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
