CREATE OR REPLACE PROCEDURE atualizar_emissao_lote_prescr ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_lote_pda_w	bigint;


BEGIN

update	ap_lote
set	dt_impressao		= clock_timestamp()
where	nr_sequencia		= nr_sequencia_p;

update	prescr_mat_hor
set	dt_emissao_farmacia	= clock_timestamp()
where	nr_seq_lote		= nr_sequencia_p;

select	count(*)
into STRICT	qt_existe_lote_pda_w
from	ap_lote_impressao_pda
where	nr_seq_lote		= nr_sequencia_p;

if (coalesce(qt_existe_lote_pda_w,0) > 0) then

	update	ap_lote_impressao_pda
	set	dt_impressao		= clock_timestamp(),
		ie_pendente		= 'N'
	where	nr_seq_lote		= nr_sequencia_p;

end if;

insert into ap_lote_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_lote,
		ds_evento,
		ds_log)
	values (	nextval('ap_lote_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(790837),
		wheb_mensagem_pck.get_texto(790837));

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
