CREATE OR REPLACE PROCEDURE atualizar_emissao_nao_padrao ( nr_prescricao_p bigint, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
nr_regras_w			integer;
cd_setor_atendimento_w		integer;
qt_item_local_w			bigint;
nr_seq_emissao_w		bigint;
ds_hora_antes_w			varchar(20);
qt_hora_antes_w			bigint;
qt_itens_w			bigint;


BEGIN

select	cd_setor_atendimento,
	coalesce(cd_estabelecimento,1)
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao		= nr_prescricao_p;

update	prescr_mat_hor a
set	a.dt_emissao_farmacia	= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	a.nr_prescricao		= nr_prescricao_p
and	coalesce(a.dt_emissao_farmacia::text, '') = ''
and	coalesce(ie_dispensar_farm,'S') = 'S'
and exists (SELECT	1
		from	prescr_material b
		where	a.nr_prescricao		= b.nr_prescricao
		and	b.nr_sequencia		= a.nr_seq_material
		and	obter_se_material_padronizado(cd_estabelecimento_w,b.cd_material) = 'N');

commit;

select	count(*)
into STRICT	qt_itens_w
from	prescr_mat_hor c,
	prescr_material b,
	prescr_medica a
where	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= nr_prescricao_p
and	b.nr_sequencia	= c.nr_seq_material
and	b.nr_prescricao	= c.nr_prescricao
and	coalesce(c.ie_dispensar_farm,'S') = 'S'
and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and	coalesce(c.dt_emissao_farmacia::text, '') = '';

if (qt_itens_w = 0) then
	update	prescr_medica
	set	dt_emissao_farmacia	= clock_timestamp(),
		nm_usuario_imp_far	= nm_usuario_p
	where	nr_prescricao		= nr_prescricao_p;

	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
