CREATE OR REPLACE PROCEDURE atualizar_emissao_prescr_hor ( nr_prescricao_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_nao_impresso_w	bigint;


BEGIN

update	prescr_mat_hor
set	dt_emissao_farmacia	= clock_timestamp(),
	nm_usuario		= nm_usuario_p||'hor'
where	nr_prescricao		= nr_prescricao_p
and	coalesce(ie_dispensar_farm,'S') = 'S'
and	((dt_horario between dt_inicio_p and dt_final_p) or (ie_urgente = 'S'));

select	count(*)
into STRICT	qt_nao_impresso_w
from	prescr_mat_hor
where	nr_prescricao	= nr_prescricao_p
and	dt_horario	> clock_timestamp()
and	coalesce(dt_emissao_farmacia::text, '') = ''
and	coalesce(ie_dispensar_farm,'S') = 'S'
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

if (qt_nao_impresso_w = 0) then
	update	prescr_medica
	set	dt_emissao_farmacia = clock_timestamp(),
		nm_usuario_imp_far  = nm_usuario_p||'hor'
	where	nr_prescricao	= nr_prescricao_p;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
