CREATE OR REPLACE PROCEDURE atualizar_emissao_prescr_proc (nr_prescricao_p bigint, nm_usuario_cor_p text) AS $body$
BEGIN

UPDATE PRESCR_PROCEDIMENTO B
SET B.DT_EMISSAO_SETOR_ATEND = clock_timestamp()
WHERE B.NR_PRESCRICAO = nr_prescricao_p
  AND coalesce(B.DT_EMISSAO_SETOR_ATEND::text, '') = ''
  AND EXISTS (SELECT 1
              from Usuario_Setor_v a
              where a.cd_setor_atendimento = b.cd_setor_atendimento
                and a.nm_usuario = nm_usuario_cor_p);

commit;
END;
