SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_emissao_prescr_turno ( nr_prescricao_p bigint, cd_local_estoque_p bigint, nr_seq_turno_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w	smallint;
nr_regras_w		integer;
cd_setor_atendimento_w	integer;
qt_item_local_w		bigint;
nr_seq_emissao_w		bigint;
ds_hora_antes_w		varchar(20);
qt_hora_antes_w		bigint;
ds_imprimi_hr_antes_w	varchar(1);


BEGIN

ds_hora_antes_w := Obter_Param_Usuario(122, 23, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ds_hora_antes_w);
ds_imprimi_hr_antes_w := Obter_Param_Usuario(122, 31, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ds_imprimi_hr_antes_w);

	qt_hora_antes_w		:= somente_numero(ds_hora_antes_w);

	select	cd_setor_atendimento,
	coalesce(cd_estabelecimento,1)
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	count(*)
into STRICT	nr_regras_w
from	regra_local_dispensacao
where	cd_estabelecimento	= cd_estabelecimento_w;

if (upper(ds_imprimi_hr_antes_w) = 'C') then
	select 	coalesce(max(qt_hora_antes),0)
	into STRICT 	qt_hora_antes_w
	from 	regra_turno_disp
	where 	nr_sequencia = nr_seq_turno_p;
end if;

if (nr_regras_w = 0) or (cd_local_estoque_p = 0) then
	begin

	update	prescr_mat_hor
	set	dt_emissao_farmacia	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_turno		= nr_seq_turno_p
	and	coalesce(ie_dispensar_farm,'S') = 'S'
	and	dt_horario		<= Obter_data_fim_turno(nr_prescricao,nr_seq_turno,qt_hora_antes_w)
	and substr(obter_se_imprimi_turno(nr_prescricao_p,nr_seq_turno_p,qt_hora_antes_w,dt_horario),1,1) = 'S'
	and	QT_DISPENSAR_HOR > 0;

	commit;

	select	count(*)
	into STRICT	qt_item_local_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(dt_emissao_farmacia::text, '') = ''
	and	coalesce(ie_dispensar_farm,'S') = 'S'
	and	QT_DISPENSAR_HOR > 0
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	if (qt_item_local_w = 0) then
		begin
		update	prescr_medica
		set	dt_emissao_farmacia 	= clock_timestamp(),
			nm_usuario_imp_far	= nm_usuario_p||'T'
		where	nr_prescricao		= nr_prescricao_p;

		update	nut_pac
		set	ie_emissao 	= 1
		where	nr_prescricao	= nr_prescricao_p;
		end;
	end if;
	end;
else
	begin

	update	prescr_mat_hor a
	set	a.dt_emissao_farmacia	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	a.nr_prescricao		= nr_prescricao_p
	and	a.ie_urgente		= 'S'
	and	coalesce(a.dt_emissao_farmacia::text, '') = ''
	and	coalesce(ie_dispensar_farm,'S') = 'S'
	and	a.QT_DISPENSAR_HOR > 0
	and exists (SELECT	1
			from	prescr_material b
			where	a.nr_prescricao		= b.nr_prescricao
			and	b.nr_sequencia		= a.nr_seq_material
			and	b.cd_local_estoque	= cd_local_estoque_p);

	commit;

	update	prescr_mat_hor a
	set	a.dt_emissao_farmacia	= clock_timestamp(),
		a.nm_usuario		= nm_usuario_p
	where	a.nr_prescricao		= nr_prescricao_p
	and	a.nr_seq_turno		= nr_seq_turno_p
	and	coalesce(ie_dispensar_farm,'S') = 'S'
	and	a.QT_DISPENSAR_HOR > 0
	and	a.dt_horario		<= Obter_data_fim_turno(a.nr_prescricao,a.nr_seq_turno,qt_hora_antes_w)
	and substr(obter_se_imprimi_turno(nr_prescricao_p,nr_seq_turno_p,qt_hora_antes_w,dt_horario),1,1) = 'S'
	and	coalesce(a.dt_emissao_farmacia::text, '') = ''
	and exists (SELECT	1
			from	prescr_material b
			where	a.nr_prescricao		= b.nr_prescricao
			and	b.nr_sequencia		= a.nr_seq_material
			and	b.cd_local_estoque	= cd_local_estoque_p);

	commit;

	select	count(*)
	into STRICT	qt_item_local_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and	coalesce(dt_emissao_farmacia::text, '') = ''
	and	coalesce(ie_dispensar_farm,'S') = 'S'
	and	QT_DISPENSAR_HOR > 0
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	if (qt_item_local_w = 0) then
		begin
		update	prescr_medica
		set	dt_emissao_farmacia 	= clock_timestamp(),
			nm_usuario_imp_far	= nm_usuario_p||'T'
		where	nr_prescricao		= nr_prescricao_p;

		update	nut_pac
		set	ie_emissao 	= 1
		where	nr_prescricao	= nr_prescricao_p;
		end;
	end if;

	end;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_emissao_prescr_turno ( nr_prescricao_p bigint, cd_local_estoque_p bigint, nr_seq_turno_p bigint, nm_usuario_p text) FROM PUBLIC;

