SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_emissao_prescr ( nr_prescricao_p bigint, cd_local_estoque_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
nr_regras_w			integer;
cd_setor_atendimento_w		integer;
qt_item_local_w			bigint;
nr_seq_emissao_w		bigint;



BEGIN

select	cd_setor_atendimento,
	coalesce(cd_estabelecimento,1)
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao		= nr_prescricao_p;

select	count(*)
into STRICT	nr_regras_w
from	regra_local_dispensacao
where	cd_estabelecimento	= cd_estabelecimento_w;

if (nr_regras_w = 0) or (cd_local_estoque_p = 0) then
	begin
	update	prescr_medica
	set	dt_emissao_farmacia 	= clock_timestamp(),
		nm_usuario_imp_far	= substr(nm_usuario_p||'1', 1, 20)
	where	nr_prescricao		= nr_prescricao_p;

	update	nut_pac
	set	ie_emissao 	= 1
	where	nr_prescricao	= nr_prescricao_p;
	end;
else
	begin
	select	nextval('prescr_emissao_local_seq')
	into STRICT	nr_seq_emissao_w
	;

	insert into Prescr_emissao_local(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_prescricao,
		cd_local_estoque)
	values (
		nr_seq_emissao_w,
		clock_timestamp(),
		substr(nm_usuario_p,1,15),
		nr_prescricao_p,
		cd_local_estoque_p);

	commit;

	select	count(*)
	into STRICT	qt_item_local_w
	from	prescr_material a
	where	a.nr_prescricao	= nr_prescricao_p
	and not exists (	SELECT	1
				from	prescr_emissao_local b
				where	a.nr_prescricao = b.nr_prescricao
				and	a.cd_local_estoque = b.cd_local_estoque);

	if (qt_item_local_w = 0) then
		begin
		update	prescr_medica
		set	dt_emissao_farmacia 	= clock_timestamp(),
			nm_usuario_imp_far	= substr(nm_usuario_p||'2', 1, 15)
		where	nr_prescricao		= nr_prescricao_p;

		update	nut_pac
		set	ie_emissao 	= 1
		where	nr_prescricao	= nr_prescricao_p;

		end;
	end if;
	end;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
